/**
 * Java 基本类型 byte (-128~127)
 */
type JByte = number;

/**
 * Java 基本类型 short (-32,768 ~ 32,767)
 */
type JShort = number;

/**
 * Java 基本类型 int (-2,147,483,648 ~ 2,147,483,647)
 */
type JInt = number;

/**
 * Java 基本类型 long (-9,223,372,036,854,774,808 ~ 9,223,372,036,854,774,807)
 */
type JLong = number;

/**
 * Java 基本类型 float
 */
type JFloat = number;

/**
 * Java 基本类型 double
 */
type JDouble = number;

/**
 * Java 基本类型 boolean
 */
type JBoolean = boolean;

/**
 * Java 基本类型 char
 */
type JChar = string;

/**
 * Java 基本类型 String
 */
type JString = string;

enum Charsets {
    US_ASCII = "US-ASCII",
    ISO_8859_1 = "ISO-8859-1",
    UTF_8 = "UTF-8",
    UTF_16BE = "UTF-16BE",
    UTF_16LE = "UTF-16LE",
    UTF_16 = "UTF-16",
}
